synchronized java.lang.String readInput(int mode, int maxValue) {
    this.mode = mode;
    this.maxValue = maxValue;
    this.currentInput.setLength(0);
    this.cancelled = false;
    if (mode == simulation.SimKeyboard.AMOUNT_MODE) {
        this.setEcho("0.00");
    } else {
        this.setEcho("");
    }
    this.requestFocus();
    try {
        this.wait();
    } catch (java.lang.InterruptedException e) {
    }
    this.mode = simulation.SimKeyboard.IDLE_MODE;
    if (this.cancelled) {
        return null;
    } else {
        return this.currentInput.toString();
    }
}

