void animateInsertion() {
    this.originalBounds = this.getBounds();
    java.awt.Rectangle currentBounds = new java.awt.Rectangle(this.originalBounds.x, this.originalBounds.y, this.originalBounds.width, this.originalBounds.height);
    while ((currentBounds.width > 0) && (currentBounds.height > 0)) {
        this.setBounds(currentBounds.x, currentBounds.y, currentBounds.width, currentBounds.height);
        this.repaint();
        try {
            java.lang.Thread.sleep(100);
        } catch (java.lang.InterruptedException e) {
        }
        currentBounds.height -= 1;
        currentBounds.width = (this.originalBounds.width * currentBounds.height) / this.originalBounds.height;
        currentBounds.x = this.originalBounds.x + ((this.originalBounds.width - currentBounds.width) / 2);
        currentBounds.y = this.originalBounds.y + ((this.originalBounds.height - currentBounds.height) / 2);
    } 
    this.setVisible(false);
}

private synchronized void enterKeyPressed() {
    switch (this.mode) {
        case simulation.SimKeyboard.IDLE_MODE :
            break;
        case simulation.SimKeyboard.PIN_MODE :
        case simulation.SimKeyboard.AMOUNT_MODE :
            if (this.currentInput.length() > 0) {
                this.notify();
            } else {
                this.getToolkit().beep();
            }
            break;
        case simulation.SimKeyboard.MENU_MODE :
            this.getToolkit().beep();
            break;
    }
}

void switchChanged(boolean on) {
    // The card reader is only enabled when the switch is on
    this.cardReader.setVisible(on);
    if (on) {
        this.atm.switchOn();
    } else {
        this.atm.switchOff();
    }
}

