// Methods corresponding to major responsibilities of the ATM
public void run() {
    atm.Session currentSession = null;
    while (true) {
        switch (this.state) {
            case atm.ATM.OFF_STATE :
                this.customerConsole.display("Not currently available");
                synchronized(this) {
                    try {
                        this.wait();
                    } catch (java.lang.InterruptedException e) {
                    }
                }
                if (this.switchOn) {
                    this.performStartup();
                    this.state = atm.ATM.IDLE_STATE;
                }
                break;
            case atm.ATM.IDLE_STATE :
                this.customerConsole.display("Please insert your card");
                this.cardInserted = false;
                synchronized(this) {
                    try {
                        this.wait();
                    } catch (java.lang.InterruptedException e) {
                    }
                }
                if (this.cardInserted) {
                    currentSession = new atm.Session(this);
                    this.state = atm.ATM.SERVING_CUSTOMER_STATE;
                } else if (!this.switchOn) {
                    this.performShutdown();
                    this.state = atm.ATM.OFF_STATE;
                }
                break;
            case atm.ATM.SERVING_CUSTOMER_STATE :
                // The following will not return until the session has
                // completed
                currentSession.performSession();
                this.state = atm.ATM.IDLE_STATE;
                break;
        }
    } 
}

synchronized int readCardNumber() {
    this.cardNumberField.setText("");
    this.cardNumberField.requestFocus();
    try {
        this.wait();
    } catch (java.lang.InterruptedException e) {
    }
    int cardNumber;
    try {
        cardNumber = java.lang.Integer.parseInt(this.cardNumberField.getText());
        if (cardNumber <= 0) {
            cardNumber = -1;
        }
    } catch (java.lang.NumberFormatException e) {
        cardNumber = -1;
    }
    return cardNumber;
}

