public int readPIN(java.lang.String prompt) throws atm.physical.CustomerConsole.Cancelled {
    simulation.Simulation.getInstance().clearDisplay();
    simulation.Simulation.getInstance().display(prompt);
    simulation.Simulation.getInstance().display("");
    java.lang.String input = simulation.Simulation.getInstance().readInput(simulation.Simulation.PIN_MODE, 0);
    simulation.Simulation.getInstance().clearDisplay();
