public static Transaction makeTransaction(ATM atm, Session session, banking.Card card, int pin) throws CustomerConsole.Cancelled {
    int choice = atm.getCustomerConsole().readMenuChoice("Please choose transaction type", Transaction.TRANSACTION_TYPES_MENU);
    switch (choice) {
        case 0 :
            return new Withdrawal(atm, session, card, pin);
        case 1 :
            return new Deposit(atm, session, card, pin);
        case 2 :
            return new Transfer(atm, session, card, pin);
        case 3 :
            return new Inquiry(atm, session, card, pin);
        default :
            return null;// To keep compiler happy - should not happen!

    }
}

