public synchronized boolean acceptEnvelope() {
    this.inserted = false;
    this.setVisible(true);
    // Wait for user to simulate inserting envelope by clicking button.
    // If we wait 20 seconds and no envelope is entered, we time out
    try {
        this.wait(simulation.SimEnvelopeAcceptor.MAXIMUM_WAIT_TIME);
    } catch (java.lang.InterruptedException e) {
    }
    if (this.inserted) {
        // Animate envelope going into the machine
        java.awt.Rectangle originalBounds = this.getBounds();
        java.awt.Rectangle currentBounds = new java.awt.Rectangle(originalBounds.x, originalBounds.y, originalBounds.width, originalBounds.height);
        while ((currentBounds.width > 0) && (currentBounds.height > 0)) {
            this.setBounds(currentBounds.x, currentBounds.y, currentBounds.width, currentBounds.height);
            this.repaint();
            try {
                java.lang.Thread.sleep(100);
            } catch (java.lang.InterruptedException e) {
            }
            currentBounds.height -= 1;
            currentBounds.width = (originalBounds.width * currentBounds.height) / originalBounds.height;
            currentBounds.x = originalBounds.x + ((originalBounds.width - currentBounds.width) / 2);
            currentBounds.y = originalBounds.y + ((originalBounds.height - currentBounds.height) / 2);
        } 
        this.setVisible(false);
        this.setBounds(originalBounds);
    } else {
        this.setVisible(false);
    }
    return this.inserted;
}

