private banking.Status transfer(banking.Message message, banking.Balances balances) {
    int cardNumber = message.getCard().getNumber();
    int fromAccountNumber = simulation.SimulatedBank.ACCOUNT_NUMBER[cardNumber][message.getFromAccount()];
    if (fromAccountNumber == 0) {
        return new simulation.SimulatedBank.Failure("Invalid from account type");
    }
    int toAccountNumber = simulation.SimulatedBank.ACCOUNT_NUMBER[cardNumber][message.getToAccount()];
    if (toAccountNumber == 0) {
        return new simulation.SimulatedBank.Failure("Invalid to account type");
    }
    if (fromAccountNumber == toAccountNumber) {
        return new simulation.SimulatedBank.Failure("Can\'t transfer money from\n" + "an account to itself");
    }
    banking.Money amount = message.getAmount();
    if (!amount.lessEqual(this.AVAILABLE_BALANCE[fromAccountNumber])) {
        return new simulation.SimulatedBank.Failure("Insufficient available balance");
    }
    // Update account balances once we know everything is OK
    this.BALANCE[fromAccountNumber].subtract(amount);
    this.AVAILABLE_BALANCE[fromAccountNumber].subtract(amount);
    this.BALANCE[toAccountNumber].add(amount);
    this.AVAILABLE_BALANCE[toAccountNumber].add(amount);
    // Return updated balances
    balances.setBalances(this.BALANCE[toAccountNumber], this.AVAILABLE_BALANCE[toAccountNumber]);
    return new simulation.SimulatedBank.Success();
}

