public banking.Status performInvalidPINExtension() throws atm.physical.CustomerConsole.Cancelled, atm.transaction.Transaction.CardRetained {
    banking.Status status = null;
    for (int i = 0; i < 3; i++) {
        this.pin = this.atm.getCustomerConsole().readPIN("PIN was incorrect\nPlease re-enter your PIN\n" + "Then press ENTER");
        this.atm.getCustomerConsole().display("");
        this.message.setPIN(this.pin);
        status = this.atm.getNetworkToBank().sendMessage(this.message, this.balances);
        if (!status.isInvalidPIN()) {
            this.session.setPIN(this.pin);
            return status;
        }
    }
    this.atm.getCardReader().retainCard();
    this.atm.getCustomerConsole().display("Your card has been retained\nPlease contact the bank.");
    try {
        java.lang.Thread.sleep(5000);
    } catch (java.lang.InterruptedException e) {
    }
    this.atm.getCustomerConsole().display("");
    throw new atm.transaction.Transaction.CardRetained();
}

