public java.util.Enumeration getLines() {
    return new java.util.Enumeration() {
        1() {
            super();
        }

        // The current portion of the receipt being printed
        private int portion = 0;

        // The index of the line in the current portion
        private int index = 0;

        public boolean hasMoreElements() {
            return (this.portion < 2) || (this.index < banking.Receipt.this.balancesPortion.length);
        }

        public java.lang.Object nextElement() {
            java.lang.String line = null;
            switch (this.portion) {
                case 0 :
                    line = banking.Receipt.this.headingPortion[this.index++];
                    if (this.index >= banking.Receipt.this.headingPortion.length) {
                        this.portion++;
                        this.index = 0;
                    }
                    break;
                case 1 :
                    line = banking.Receipt.this.detailsPortion[this.index++];
                    if (this.index >= banking.Receipt.this.detailsPortion.length) {
                        this.portion++;
                        this.index = 0;
                    }
                    break;
                case 2 :
                    line = banking.Receipt.this.balancesPortion[this.index++];
                    break;
            }
            return line;
        }
    };
}

