public void performSession() {
    banking.Card card = null;
    atm.transaction.Transaction currentTransaction = null;
    while (this.state != atm.Session.FINAL_STATE) {
        switch (this.state) {
            case atm.Session.READING_CARD_STATE :
                card = this.atm.getCardReader().readCard();
                if (card != null) {
                    this.state = atm.Session.READING_PIN_STATE;
                } else {
                    this.atm.getCustomerConsole().display("Unable to read card");
                    this.state = atm.Session.EJECTING_CARD_STATE;
                }
                break;
            case atm.Session.READING_PIN_STATE :
                try {
                    this.pin = this.atm.getCustomerConsole().readPIN("Please enter your PIN\n" + "Then press ENTER");
                    this.state = atm.Session.CHOOSING_TRANSACTION_STATE;
                } catch (atm.physical.CustomerConsole.Cancelled e) {
                    this.state = atm.Session.EJECTING_CARD_STATE;
                }
                break;
            case atm.Session.CHOOSING_TRANSACTION_STATE :
                try {
                    currentTransaction = atm.transaction.Transaction.makeTransaction(this.atm, this, card, this.pin);
                    this.state = atm.Session.PERFORMING_TRANSACTION_STATE;
                } catch (atm.physical.CustomerConsole.Cancelled e) {
                    this.state = atm.Session.EJECTING_CARD_STATE;
                }
                break;
            case atm.Session.PERFORMING_TRANSACTION_STATE :
                try {
                    boolean doAgain = currentTransaction.performTransaction();
                    if (doAgain) {
                        this.state = atm.Session.CHOOSING_TRANSACTION_STATE;
                    } else {
                        this.state = atm.Session.EJECTING_CARD_STATE;
                    }
                } catch (atm.transaction.Transaction.CardRetained e) {
                    this.state = atm.Session.FINAL_STATE;
                }
                break;
            case atm.Session.EJECTING_CARD_STATE :
                this.atm.getCardReader().ejectCard();
                this.state = atm.Session.FINAL_STATE;
                break;
        }
    } 
}

