public java.lang.String toString() {
    java.lang.String result = "";
    switch (this.messageCode) {
        case banking.Message.WITHDRAWAL :
            result += "WITHDRAW";
            break;
        case banking.Message.INITIATE_DEPOSIT :
            result += "INIT_DEP";
            break;
        case banking.Message.COMPLETE_DEPOSIT :
            result += "COMP_DEP";
            break;
        case banking.Message.TRANSFER :
            result += "TRANSFER";
            break;
        case banking.Message.INQUIRY :
            result += "INQUIRY ";
            break;
    }
    result += " CARD# " + this.card.getNumber();
    result += " TRANS# " + this.serialNumber;
    if (this.fromAccount >= 0) {
        result += " FROM  " + this.fromAccount;
    } else {
        result += " NO FROM";
    }
    if (this.toAccount >= 0) {
        result += " TO  " + this.toAccount;
    } else {
        result += " NO TO";
    }
    if (!this.amount.lessEqual(new banking.Money(0))) {
        result += " " + this.amount;
    } else {
        result += " NO AMOUNT";
    }
    return result;
}

void display(java.lang.String text) {
