public banking.Status handleMessage(banking.Message message, banking.Balances balances) {
    int cardNumber = message.getCard().getNumber();
    if ((cardNumber < 1) || (cardNumber > simulation.SimulatedBank.PIN.length)) {
        return new simulation.SimulatedBank.Failure("Invalid card");
    }
    if (message.getPIN() != simulation.SimulatedBank.PIN[cardNumber]) {
        return new simulation.SimulatedBank.InvalidPIN();
    }
    switch (message.getMessageCode()) {
        case banking.Message.WITHDRAWAL :
            return this.withdrawal(message, balances);
        case banking.Message.INITIATE_DEPOSIT :
            return this.initiateDeposit(message);
        case banking.Message.COMPLETE_DEPOSIT :
            return this.completeDeposit(message, balances);
        case banking.Message.TRANSFER :
            return this.transfer(message, balances);
        case banking.Message.INQUIRY :
            return this.inquiry(message, balances);
    }
    // Need to keep compiler happy
    return null;
}

