public synchronized banking.Money readAmount(java.lang.String prompt) throws atm.physical.CustomerConsole.Cancelled {
    simulation.Simulation.getInstance().clearDisplay();
    simulation.Simulation.getInstance().display(prompt);
    simulation.Simulation.getInstance().display("");
    java.lang.String input = simulation.Simulation.getInstance().readInput(simulation.Simulation.AMOUNT_MODE, 0);
    simulation.Simulation.getInstance().clearDisplay();
    if (input == null) {
        throw new atm.physical.CustomerConsole.Cancelled();
    } else {
        int dollars = java.lang.Integer.parseInt(input) / 100;
        int cents = java.lang.Integer.parseInt(input) % 100;
        return new banking.Money(dollars, cents);
    }
}

