private synchronized void digitKeyPressed(int digit) {
    switch (this.mode) {
        case simulation.SimKeyboard.IDLE_MODE :
            break;
        case simulation.SimKeyboard.PIN_MODE :
            {
                this.currentInput.append(digit);
                java.lang.StringBuffer echoString = new java.lang.StringBuffer();
                for (int i = 0; i < this.currentInput.length(); i++) {
                    echoString.append('*');
                }
                this.setEcho(echoString.toString());
                break;
            }
        case simulation.SimKeyboard.AMOUNT_MODE :
            {
                this.currentInput.append(digit);
                java.lang.String input = this.currentInput.toString();
                if (input.length() == 1) {
                    this.setEcho("0.0" + input);
                } else if (input.length() == 2) {
                    this.setEcho("0." + input);
                } else {
                    this.setEcho((input.substring(0, input.length() - 2) + ".") + input.substring(input.length() - 2));
                }
                break;
            }
        case simulation.SimKeyboard.MENU_MODE :
            {
                if ((digit > 0) && (digit <= this.maxValue)) {
                    this.currentInput.append(digit);
                    this.notify();
                } else {
                    this.getToolkit().beep();
                }
                break;
            }
    }
}

