SimOperatorPanel(final Simulation simulation) {
    super();
    this.setLayout(new java.awt.BorderLayout(10, 0));
    this.setBackground(new java.awt.Color(128, 128, 255));
    this.add(new java.awt.Label("     Operator Panel"), java.awt.BorderLayout.WEST);
    final java.awt.Label message = new java.awt.Label("Click button to turn ATM on", java.awt.Label.CENTER);
    this.add(message, java.awt.BorderLayout.CENTER);
    final java.awt.Button button = new java.awt.Button(" ON ");
    button.addActionListener(new java.awt.event.ActionListener() {
        1() {
            super();
        }

        public void actionPerformed(java.awt.event.ActionEvent e) {
            // ATM is currently on
            if (button.getLabel().equals("OFF")) {
                message.setText("Click button to turn ATM on  ");
                button.setLabel(" ON ");
                simulation.switchChanged(false);
            } else // ATM is currently off
            {
                message.setText("Click button to turn ATM off");
                button.setLabel("OFF");
                simulation.switchChanged(true);
            }
        }
    });
    java.awt.Panel buttonPanel = new java.awt.Panel();
    buttonPanel.add(button);
    this.add(buttonPanel, java.awt.BorderLayout.EAST);
    // Use a thread to blink the "Click button to turn ATM on" message when
    // the ATM is off.  This will also make the message invisible when the
    // button is not enabled.
    new java.lang.Thread() {
        2() {
            super();
        }

        public void run() {
            while (true) {
                try {
                    java.lang.Thread.sleep(1000);
                } catch (java.lang.InterruptedException e) {
                }
                if ((message.isVisible() && (!button.getLabel().equals("OFF"))) || (!SimOperatorPanel.this.isEnabled())) {
                    message.setVisible(false);
                } else {
                    message.setVisible(true);
                }
            } 
        }
    }.start();
}

