private banking.Status withdrawal(banking.Message message, banking.Balances balances) {
    int cardNumber = message.getCard().getNumber();
    int accountNumber = simulation.SimulatedBank.ACCOUNT_NUMBER[cardNumber][message.getFromAccount()];
    if (accountNumber == 0) {
        return new simulation.SimulatedBank.Failure("Invalid account type");
    }
    banking.Money amount = message.getAmount();
    banking.Money limitRemaining = new banking.Money(simulation.SimulatedBank.DAILY_WITHDRAWAL_LIMIT);
    limitRemaining.subtract(simulation.SimulatedBank.WITHDRAWALS_TODAY[cardNumber]);
    if (!amount.lessEqual(limitRemaining)) {
        return new simulation.SimulatedBank.Failure("Daily withdrawal limit exceeded");
    }
    if (!amount.lessEqual(this.AVAILABLE_BALANCE[accountNumber])) {
        return new simulation.SimulatedBank.Failure("Insufficient available balance");
    }
    // Update withdrawals today and account balances once we know everything is
    // OK
    simulation.SimulatedBank.WITHDRAWALS_TODAY[cardNumber].add(amount);
    this.BALANCE[accountNumber].subtract(amount);
    this.AVAILABLE_BALANCE[accountNumber].subtract(amount);
    // Return updated balances
    balances.setBalances(this.BALANCE[accountNumber], this.AVAILABLE_BALANCE[accountNumber]);
    return new simulation.SimulatedBank.Success();
}

