private banking.Status completeDeposit(banking.Message message, banking.Balances balances) {
    int cardNumber = message.getCard().getNumber();
    int accountNumber = simulation.SimulatedBank.ACCOUNT_NUMBER[cardNumber][message.getToAccount()];
    if (accountNumber == 0) {
        return new simulation.SimulatedBank.Failure("Invalid account type");
    }
    // Now we can update the balance
    banking.Money amount = message.getAmount();
    this.BALANCE[accountNumber].add(amount);
    // Return updated balances
    balances.setBalances(this.BALANCE[accountNumber], this.AVAILABLE_BALANCE[accountNumber]);
    return new simulation.SimulatedBank.Success();
}

