private synchronized void cancelKeyPressed() {
    switch (this.mode) {
        case simulation.SimKeyboard.IDLE_MODE :
            // It is possible to press the cancel key when requested
            // to insert an envelope - so notify the envelope acceptor
            // of this fact (notification is ignored if acceptor is
            // not waiting for an envelope)
            synchronized(this.envelopeAcceptor) {
                this.envelopeAcceptor.notify();
            }
        case simulation.SimKeyboard.PIN_MODE :
        case simulation.SimKeyboard.AMOUNT_MODE :
        case simulation.SimKeyboard.MENU_MODE :
            this.cancelled = true;
            this.notify();
    }
}

SimKeyboard(simulation.SimDisplay display, simulation.SimEnvelopeAcceptor envelopeAcceptor) {
    super();
    this.display = display;
    this.envelopeAcceptor = envelopeAcceptor;
    this.setLayout(new java.awt.GridLayout(5, 3));
    // Create the keys representing digits
    java.awt.Button[] digitKey = new java.awt.Button[10];
    for (int i = 1; i < 10; i++) {
        digitKey[i] = new java.awt.Button("" + i);
        this.add(digitKey[i]);
    }
    this.add(new java.awt.Label(""));
    digitKey[0] = new java.awt.Button("0");
    this.add(digitKey[0]);
    this.add(new java.awt.Label(""));
    // Create the function keys
    java.awt.Button enterKey = new java.awt.Button("ENTER");
    enterKey.setForeground(java.awt.Color.black);
    enterKey.setBackground(new java.awt.Color(128, 128, 255));// Light blue

    this.add(enterKey);
    java.awt.Button clearKey = new java.awt.Button("CLEAR");
    clearKey.setForeground(java.awt.Color.black);
    clearKey.setBackground(new java.awt.Color(255, 128, 128));// Light red

    this.add(clearKey);
    java.awt.Button cancelKey = new java.awt.Button("CANCEL");
    cancelKey.setBackground(java.awt.Color.red);
    cancelKey.setForeground(java.awt.Color.black);
    this.add(cancelKey);
    // Add action listeners to each key
    for (int i = 0; i < 10; i++) {
        digitKey[i].addActionListener(new java.awt.event.ActionListener() {
            1() {
                super();
            }

            public void actionPerformed(java.awt.event.ActionEvent e) {
                this.digitKeyPressed(java.lang.Integer.parseInt(e.getActionCommand()));
            }
        });
    }
    enterKey.addActionListener(new java.awt.event.ActionListener() {
        2() {
            super();
        }

        public void actionPerformed(java.awt.event.ActionEvent e) {
            this.enterKeyPressed();
        }
    });
    clearKey.addActionListener(new java.awt.event.ActionListener() {
        3() {
            super();
        }

        public void actionPerformed(java.awt.event.ActionEvent e) {
            this.clearKeyPressed();
        }
    });
    cancelKey.addActionListener(new java.awt.event.ActionListener() {
        4() {
            super();
        }

        public void actionPerformed(java.awt.event.ActionEvent e) {
            this.cancelKeyPressed();
        }
    });
    // Allow the user to use the regular keyboard instead of the awt keyboard
    this.addKeyListener(new java.awt.event.KeyAdapter() {
        5() {
            super();
        }

        public void keyPressed(java.awt.event.KeyEvent e) {
            char keyChar = e.getKeyChar();
            int keyCode = e.getKeyCode();
            if ((keyChar >= '0') && (keyChar <= '9')) {
                this.digitKeyPressed(keyChar - '0');
                e.consume();
            } else {
                switch (keyCode) {
                    case java.awt.event.KeyEvent.VK_ENTER :
                        this.enterKeyPressed();
                        break;
                    case java.awt.event.KeyEvent.VK_CLEAR :
                        this.clearKeyPressed();
                        break;
                    case java.awt.event.KeyEvent.VK_CANCEL :
                    case java.awt.event.KeyEvent.VK_ESCAPE :
                        this.cancelKeyPressed();
                        break;
                }
                e.consume();
            }
        }
    });
    // Initialze mode and input buffer
    this.currentInput = new java.lang.StringBuffer();
    this.mode = simulation.SimKeyboard.IDLE_MODE;
}

