public boolean performTransaction() throws atm.transaction.Transaction.CardRetained {
    java.lang.String doAnotherMessage = "";
    banking.Status status = null;
    banking.Receipt receipt = null;
    // Terminates by return in ASKING_DO_ANOTHER_STATE or exception
    while (true) {
        switch (this.state) {
            case atm.transaction.Transaction.GETTING_SPECIFICS_STATE :
                try {
                    this.message = this.getSpecificsFromCustomer();
                    this.atm.getCustomerConsole().display("");
                    this.state = atm.transaction.Transaction.SENDING_TO_BANK_STATE;
                } catch (atm.physical.CustomerConsole.Cancelled e) {
                    doAnotherMessage = "Last transaction was cancelled";
                    this.state = atm.transaction.Transaction.ASKING_DO_ANOTHER_STATE;
                }
                break;
            case atm.transaction.Transaction.SENDING_TO_BANK_STATE :
                status = this.atm.getNetworkToBank().sendMessage(this.message, this.balances);
                if (status.isInvalidPIN()) {
                    this.state = atm.transaction.Transaction.INVALID_PIN_STATE;
                } else if (status.isSuccess()) {
                    this.state = atm.transaction.Transaction.COMPLETING_TRANSACTION_STATE;
                } else {
                    doAnotherMessage = status.getMessage();
                    this.state = atm.transaction.Transaction.ASKING_DO_ANOTHER_STATE;
                }
                break;
            case atm.transaction.Transaction.INVALID_PIN_STATE :
                try {
                    status = this.performInvalidPINExtension();
                    // If customer repeatedly enters invalid PIN's, a
                    // CardRetained exception is thrown, and this method
                    // terminates
                    if (status.isSuccess()) {
                        this.state = atm.transaction.Transaction.COMPLETING_TRANSACTION_STATE;
                    } else {
                        doAnotherMessage = status.getMessage();
                        this.state = atm.transaction.Transaction.ASKING_DO_ANOTHER_STATE;
                    }
                } catch (atm.physical.CustomerConsole.Cancelled e) {
                    doAnotherMessage = "Last transaction was cancelled";
                    this.state = atm.transaction.Transaction.ASKING_DO_ANOTHER_STATE;
                }
                break;
            case atm.transaction.Transaction.COMPLETING_TRANSACTION_STATE :
                try {
                    receipt = this.completeTransaction();
                    this.state = atm.transaction.Transaction.PRINTING_RECEIPT_STATE;
                } catch (atm.physical.CustomerConsole.Cancelled e) {
                    doAnotherMessage = "Last transaction was cancelled";
                    this.state = atm.transaction.Transaction.ASKING_DO_ANOTHER_STATE;
                }
                break;
            case atm.transaction.Transaction.PRINTING_RECEIPT_STATE :
                this.atm.getReceiptPrinter().printReceipt(receipt);
                this.state = atm.transaction.Transaction.ASKING_DO_ANOTHER_STATE;
                break;
            case atm.transaction.Transaction.ASKING_DO_ANOTHER_STATE :
                if (doAnotherMessage.length() > 0) {
                    doAnotherMessage += "\n";
                }
                try {
                    java.lang.String[] yesNoMenu = new java.lang.String[]{ "Yes", "No" };
                    boolean doAgain = this.atm.getCustomerConsole().readMenuChoice(doAnotherMessage + "Would you like to do another transaction?", yesNoMenu) == 0;
                    return doAgain;
                } catch (atm.physical.CustomerConsole.Cancelled e) {
                    return false;
                }
        }
    } 
}

