/**
 * Returns a list of all classes and interfaces the class inherits.
 *
 * @param clazz
 * 		The class to create a list of super classes and interfaces
 * 		for.
 * @return A list of all classes and interfaces the class inherits.
 */
@java.lang.SuppressWarnings("unchecked")
private java.util.List<java.lang.Class<? extends nl.tudelft.jpacman.board.Unit>> getInheritance(java.lang.Class<? extends nl.tudelft.jpacman.board.Unit> clazz) {
    java.util.List<java.lang.Class<? extends nl.tudelft.jpacman.board.Unit>> found = new java.util.ArrayList<java.lang.Class<? extends nl.tudelft.jpacman.board.Unit>>();
    found.add(clazz);
    int index = 0;
    while (found.size() > index) {
        java.lang.Class<? extends java.lang.Object> current = found.get(index);
        java.lang.Class<? extends java.lang.Object> superClass = current.getSuperclass();
        if ((superClass != null) && nl.tudelft.jpacman.board.Unit.class.isAssignableFrom(superClass)) {
            found.add(((java.lang.Class<? extends nl.tudelft.jpacman.board.Unit>) (superClass)));
        }
        for (java.lang.Class<? extends java.lang.Object> classInterface : current.getInterfaces()) {
            if (nl.tudelft.jpacman.board.Unit.class.isAssignableFrom(classInterface)) {
                found.add(((java.lang.Class<? extends nl.tudelft.jpacman.board.Unit>) (classInterface)));
            }
        }
        index++;
    } 
    return found;
}

