/**
 * Figures out the most specific class that is listed in the map. I.e. if A
 * extends B and B is listed while requesting A, then B will be returned.
 *
 * @param map
 * 		The map with the key collection to find a matching class in.
 * @param key
 * 		The class to search the most suitable key for.
 * @return The most specific class from the key collection.
 */
private java.lang.Class<? extends nl.tudelft.jpacman.board.Unit> getMostSpecificClass(java.util.Map<java.lang.Class<? extends nl.tudelft.jpacman.board.Unit>, ? extends java.lang.Object> map, java.lang.Class<? extends nl.tudelft.jpacman.board.Unit> key) {
    java.util.List<java.lang.Class<? extends nl.tudelft.jpacman.board.Unit>> collideeInheritance = this.getInheritance(key);
    for (java.lang.Class<? extends nl.tudelft.jpacman.board.Unit> pointer : collideeInheritance) {
        if (map.containsKey(pointer)) {
            return pointer;
        }
    }
    return null;
}

