@java.lang.Override
public nl.tudelft.jpacman.sprite.Sprite split(int x, int y, int width, int height) {
    if (this.withinImage(x, y) && this.withinImage((x + width) - 1, (y + height) - 1)) {
        java.awt.image.BufferedImage newImage = this.newImage(width, height);
        newImage.createGraphics().drawImage(this.image, 0, 0, width, height, x, y, x + width, y + height, null);
        return new nl.tudelft.jpacman.sprite.ImageSprite(newImage);
    }
    return new nl.tudelft.jpacman.sprite.EmptySprite();
}

