/**
 * Starts or resumes this level, allowing movement and (re)starting the
 * NPCs.
 */
public void start() {
    synchronized(this.startStopLock) {
        if (this.isInProgress()) {
            return;
        }
        this.startNPCs();
        this.inProgress = true;
        this.updateObservers();
    }
}

private nl.tudelft.jpacman.board.Square followPath(java.util.List<nl.tudelft.jpacman.board.Direction> directions, nl.tudelft.jpacman.board.Square start) {
    nl.tudelft.jpacman.board.Square destination = start;
    for (nl.tudelft.jpacman.board.Direction d : directions) {
        destination = destination.getSquareAt(d);
    }
    return destination;
}

