/**
 * Finds the nearest unit of the given type and returns its location. This
 * method will perform a breadth first search starting from the given
 * square.
 *
 * @param type
 * 		The type of unit to search for.
 * @param currentLocation
 * 		The starting location for the search.
 * @return The nearest unit of the given type, or <code>null</code> if no
such unit could be found.
 */
public static nl.tudelft.jpacman.board.Unit findNearest(java.lang.Class<? extends nl.tudelft.jpacman.board.Unit> type, nl.tudelft.jpacman.board.Square currentLocation) {
    java.util.List<nl.tudelft.jpacman.board.Square> toDo = new java.util.ArrayList<nl.tudelft.jpacman.board.Square>();
    java.util.Set<nl.tudelft.jpacman.board.Square> visited = new java.util.HashSet<nl.tudelft.jpacman.board.Square>();
    toDo.add(currentLocation);
    while (!toDo.isEmpty()) {
        nl.tudelft.jpacman.board.Square square = toDo.remove(0);
        nl.tudelft.jpacman.board.Unit unit = nl.tudelft.jpacman.npc.ghost.Navigation.findUnit(type, square);
        if (unit != null) {
            assert unit.hasSquare();
            return unit;
        }
        visited.add(square);
        for (nl.tudelft.jpacman.board.Direction direction : nl.tudelft.jpacman.board.Direction.values()) {
            nl.tudelft.jpacman.board.Square newTarget = square.getSquareAt(direction);
            if ((!visited.contains(newTarget)) && (!toDo.contains(newTarget))) {
                toDo.add(newTarget);
            }
        }
    } 
    return null;
}

