/**
 * Check the correctness of the map lines in the text.
 *
 * @param text
 * 		Map to be checked
 * @throws PacmanConfigurationException
 * 		if map is not OK.
 */
private void checkMapFormat(java.util.List<java.lang.String> text) {
    if (text == null) {
        throw new nl.tudelft.jpacman.PacmanConfigurationException("Input text cannot be null.");
    }
    if (text.isEmpty()) {
        throw new nl.tudelft.jpacman.PacmanConfigurationException("Input text must consist of at least 1 row.");
    }
    int width = text.get(0).length();
    if (width == 0) {
        throw new nl.tudelft.jpacman.PacmanConfigurationException("Input text lines cannot be empty.");
    }
    for (java.lang.String line : text) {
        if (line.length() != width) {
            throw new nl.tudelft.jpacman.PacmanConfigurationException("Input text lines are not of equal width.");
        }
    }
}

