    java.lang.Iterable<nl.tudelft.jpacman.level.Level.LevelObserver> observers = new java.util.ArrayList<nl.tudelft.jpacman.level.Level.LevelObserver>(this.observers);
    if (!this.isAnyPlayerAlive()) {
        for (nl.tudelft.jpacman.level.Level.LevelObserver observer : observers) {
            observer.levelLost();
        }
    }
    if (this.remainingPellets() == 0) {
        for (nl.tudelft.jpacman.level.Level.LevelObserver observer : observers) {
            observer.levelWon();
