/**
 * Moves the unit into the given direction if possible and handles all
 * collisions.
 *
 * @param unit
 * 		The unit to move.
 * @param direction
 * 		The direction to move the unit in.
 */
public void move(nl.tudelft.jpacman.board.Unit unit, nl.tudelft.jpacman.board.Direction direction) {
    assert unit != null;
    assert direction != null;
    assert unit.hasSquare();
    if (!this.isInProgress()) {
        return;
    }
    synchronized(this.moveLock) {
        unit.setDirection(direction);
        nl.tudelft.jpacman.board.Square location = unit.getSquare();
        nl.tudelft.jpacman.board.Square destination = location.getSquareAt(direction);
        if (destination.isAccessibleTo(unit)) {
            java.util.List<nl.tudelft.jpacman.board.Unit> occupants = destination.getOccupants();
            unit.occupy(destination);
            for (nl.tudelft.jpacman.board.Unit occupant : occupants) {
                this.collisions.collide(unit, occupant);
            }
        }
        this.updateObservers();
    }
}

