/**
 * Adds a square to the grid based on a given character. These
 * character come from the map files and describe the type
 * of square.
 *
 * @param grid
 * 		The grid of squares with board[x][y] being the
 * 		square at column x, row y.
 * @param ghosts
 * 		List of all ghosts that were added to the map.
 * @param startPositions
 * 		List of all start positions that were added
 * 		to the map.
 * @param x
 * 		x coordinate of the square.
 * @param y
 * 		y coordinate of the square.
 * @param c
 * 		Character describing the square type.
 */
protected void addSquare(nl.tudelft.jpacman.board.Square[][] grid, java.util.List<nl.tudelft.jpacman.npc.Ghost> ghosts, java.util.List<nl.tudelft.jpacman.board.Square> startPositions, int x, int y, char c) {
    switch (c) {
        case ' ' :
            grid[x][y] = this.boardCreator.createGround();
            break;
        case '#' :
            grid[x][y] = this.boardCreator.createWall();
            break;
        case '.' :
            nl.tudelft.jpacman.board.Square pelletSquare = this.boardCreator.createGround();
            grid[x][y] = pelletSquare;
            this.levelCreator.createPellet().occupy(pelletSquare);
            break;
        case 'G' :
            nl.tudelft.jpacman.board.Square ghostSquare = this.makeGhostSquare(ghosts, this.levelCreator.createGhost());
            grid[x][y] = ghostSquare;
            break;
        case 'P' :
            nl.tudelft.jpacman.board.Square playerSquare = this.boardCreator.createGround();
            grid[x][y] = playerSquare;
            startPositions.add(playerSquare);
            break;
        default :
            throw new nl.tudelft.jpacman.PacmanConfigurationException((((("Invalid character at " + x) + ",") + y) + ": ") + c);
    }
}

