/**
 * Handles the collision between two colliding parties, if a suitable
 * collision handler is listed.
 *
 * @param <C1>
 * 		The collider type.
 * @param <C2>
 * 		The collidee (unit that was moved into) type.
 * @param collider
 * 		The collider.
 * @param collidee
 * 		The collidee.
 */
@java.lang.SuppressWarnings("unchecked")
@java.lang.Override
public <C1 extends nl.tudelft.jpacman.board.Unit, C2 extends nl.tudelft.jpacman.board.Unit> void collide(C1 collider, C2 collidee) {
    java.lang.Class<? extends nl.tudelft.jpacman.board.Unit> colliderKey = this.getMostSpecificClass(this.handlers, collider.getClass());
    if (colliderKey == null) {
        return;
    }
    java.util.Map<java.lang.Class<? extends nl.tudelft.jpacman.board.Unit>, nl.tudelft.jpacman.level.CollisionInteractionMap.CollisionHandler<? extends java.lang.Object, ? extends java.lang.Object>> map = this.handlers.get(colliderKey);
    java.lang.Class<? extends nl.tudelft.jpacman.board.Unit> collideeKey = this.getMostSpecificClass(map, collidee.getClass());
    if (collideeKey == null) {
        return;
    }
    nl.tudelft.jpacman.level.CollisionInteractionMap.CollisionHandler<C1, C2> collisionHandler = ((nl.tudelft.jpacman.level.CollisionInteractionMap.CollisionHandler<C1, C2>) (map.get(collideeKey)));
    if (collisionHandler == null) {
        return;
    }
    collisionHandler.handleCollision(collider, collidee);
}

