    for (final nl.tudelft.jpacman.npc.Ghost npc : this.npcs.keySet()) {
        java.util.concurrent.ScheduledExecutorService service = java.util.concurrent.Executors.newSingleThreadScheduledExecutor();
        service.schedule(new nl.tudelft.jpacman.level.Level.NpcMoveTask(service, npc), npc.getInterval() / 2, java.util.concurrent.TimeUnit.MILLISECONDS);
        this.npcs.put(npc, service);
