/**
 * Creates a new {@link AnimatedSprite} from a base image.
 *
 * @param baseImage
 * 		The base image to convert into an animation.
 * @param frames
 * 		The amount of frames of the animation.
 * @param delay
 * 		The delay between frames.
 * @param loop
 * 		Whether this sprite is a looping animation or not.
 * @return The animated sprite.
 */
public nl.tudelft.jpacman.sprite.AnimatedSprite createAnimatedSprite(nl.tudelft.jpacman.sprite.Sprite baseImage, int frames, int delay, boolean loop) {
    assert baseImage != null;
    assert frames > 0;
    int frameWidth = baseImage.getWidth() / frames;
    nl.tudelft.jpacman.sprite.Sprite[] animation = new nl.tudelft.jpacman.sprite.Sprite[frames];
    for (int i = 0; i < frames; i++) {
        animation[i] = baseImage.split(i * frameWidth, 0, frameWidth, baseImage.getHeight());
    }
    return new nl.tudelft.jpacman.sprite.AnimatedSprite(animation, delay, loop);
}

