/**
 * Counts the pellets remaining on the board.
 *
 * @return The amount of pellets remaining on the board.
 */
public int remainingPellets() {
    nl.tudelft.jpacman.board.Board board = this.getBoard();
    int pellets = 0;
    for (int x = 0; x < board.getWidth(); x++) {
        for (int y = 0; y < board.getHeight(); y++) {
            for (nl.tudelft.jpacman.board.Unit unit : board.squareAt(x, y).getOccupants()) {
                if (unit instanceof nl.tudelft.jpacman.level.Pellet) {
                    pellets++;
                }
            }
        }
    }
    assert pellets >= 0;
    return pellets;
}

