    nl.tudelft.jpacman.level.CollisionInteractionMap collisionMap = new nl.tudelft.jpacman.level.CollisionInteractionMap();
    collisionMap.onCollision(nl.tudelft.jpacman.level.Player.class, nl.tudelft.jpacman.npc.Ghost.class, (nl.tudelft.jpacman.level.Player player,nl.tudelft.jpacman.npc.Ghost ghost) -> {
        this.pointCalculator.collidedWithAGhost(player, ghost);
        player.setAlive(false);
        player.setKiller(ghost);
