    collisionMap.onCollision(nl.tudelft.jpacman.level.Player.class, nl.tudelft.jpacman.level.Pellet.class, (nl.tudelft.jpacman.level.Player player,nl.tudelft.jpacman.level.Pellet pellet) -> {
        this.pointCalculator.consumedAPellet(player, pellet);
        pellet.leaveSquare();
    });
    return collisionMap;
}

