/**
 * Calculates the shortest path. This is done by BFS. This search ensures
 * the traveller is allowed to occupy the squares on the way, or returns the
 * shortest path to the square regardless of terrain if no traveller is
 * specified.
 *
 * @param from
 * 		The starting square.
 * @param to
 * 		The destination.
 * @param traveller
 * 		The traveller attempting to reach the destination. If
 * 		traveller is set to <code>null</code>, this method will ignore
 * 		terrain and find the shortest path whether it can actually be
 * 		reached or not.
 * @return The shortest path to the destination or <code>null</code> if no
such path could be found. When the destination is the current
square, an empty list is returned.
 */
public static java.util.List<nl.tudelft.jpacman.board.Direction> shortestPath(nl.tudelft.jpacman.board.Square from, nl.tudelft.jpacman.board.Square to, nl.tudelft.jpacman.board.Unit traveller) {
    if (from.equals(to)) {
        return new java.util.ArrayList<nl.tudelft.jpacman.board.Direction>();
    }
    java.util.List<nl.tudelft.jpacman.npc.ghost.Navigation.Node> targets = new java.util.ArrayList<nl.tudelft.jpacman.npc.ghost.Navigation.Node>();
    java.util.Set<nl.tudelft.jpacman.board.Square> visited = new java.util.HashSet<nl.tudelft.jpacman.board.Square>();
    targets.add(new nl.tudelft.jpacman.npc.ghost.Navigation.Node(null, from, null));
    while (!targets.isEmpty()) {
        nl.tudelft.jpacman.npc.ghost.Navigation.Node node = targets.remove(0);
        nl.tudelft.jpacman.board.Square square = node.getSquare();
        if (square.equals(to)) {
            return node.getPath();
        }
        visited.add(square);
        nl.tudelft.jpacman.npc.ghost.Navigation.addNewTargets(traveller, targets, visited, node, square);
    } 
    return null;
}

