/**
 * Creates a new board from a grid of cells and connects it.
 *
 * @param grid
 * 		The square grid of cells, in which grid[x][y] corresponds to
 * 		the square at position x,y.
 * @return A new board, wrapping a grid of connected cells.
 */
public nl.tudelft.jpacman.board.Board createBoard(nl.tudelft.jpacman.board.Square[][] grid) {
    assert grid != null;
    nl.tudelft.jpacman.board.Board board = new nl.tudelft.jpacman.board.Board(grid);
    int width = board.getWidth();
    int height = board.getHeight();
    for (int x = 0; x < width; x++) {
        for (int y = 0; y < height; y++) {
            nl.tudelft.jpacman.board.Square square = grid[x][y];
            for (nl.tudelft.jpacman.board.Direction dir : nl.tudelft.jpacman.board.Direction.values()) {
                int dirX = ((width + x) + dir.getDeltaX()) % width;
                int dirY = ((height + y) + dir.getDeltaY()) % height;
                nl.tudelft.jpacman.board.Square neighbour = grid[dirX][dirY];
                square.link(neighbour, dir);
            }
        }
    }
    return board;
}

