/**
 * Registers a player on this level, assigning him to a starting position. A
 * player can only be registered once, registering a player again will have
 * no effect.
 *
 * @param player
 * 		The player to register.
 */
public void registerPlayer(nl.tudelft.jpacman.level.Player player) {
    assert player != null;
    assert !this.startSquares.isEmpty();
    if (this.players.contains(player)) {
        return;
    }
    this.players.add(player);
    nl.tudelft.jpacman.board.Square square = this.startSquares.get(this.startSquareIndex);
    player.occupy(square);
    this.startSquareIndex++;
    this.startSquareIndex %= this.startSquares.size();
}

/**
 * Creates a new UI for a JPacman game.
 *
 * @param game
 * 		The game to play.
 * @param buttons
 * 		The map of caption-to-action entries that will appear as
 * 		buttons on the interface.
 * @param keyMappings
 * 		The map of keyCode-to-action entries that will be added as key
 * 		listeners to the interface.
 * @param scoreFormatter
 * 		The formatter used to display the current score.
 */
public PacManUI(final nl.tudelft.jpacman.game.Game game, final java.util.Map<java.lang.String, nl.tudelft.jpacman.ui.Action> buttons, final java.util.Map<java.lang.Integer, nl.tudelft.jpacman.ui.Action> keyMappings, nl.tudelft.jpacman.ui.ScorePanel.ScoreFormatter scoreFormatter) {
    super("JPacman");
    assert game != null;
    assert buttons != null;
    assert keyMappings != null;
    this.setDefaultCloseOperation(javax.swing.WindowConstants.EXIT_ON_CLOSE);
    nl.tudelft.jpacman.ui.PacKeyListener keys = new nl.tudelft.jpacman.ui.PacKeyListener(keyMappings);
    this.addKeyListener(keys);
    javax.swing.JPanel buttonPanel = new nl.tudelft.jpacman.ui.ButtonPanel(buttons, this);
    this.scorePanel = new nl.tudelft.jpacman.ui.ScorePanel(game.getPlayers());
    if (scoreFormatter != null) {
        this.scorePanel.setScoreFormatter(scoreFormatter);
    }
    this.boardPanel = new nl.tudelft.jpacman.ui.BoardPanel(game);
    java.awt.Container contentPanel = this.getContentPane();
    contentPanel.setLayout(new java.awt.BorderLayout());
    contentPanel.add(buttonPanel, java.awt.BorderLayout.SOUTH);
    contentPanel.add(this.scorePanel, java.awt.BorderLayout.NORTH);
    contentPanel.add(this.boardPanel, java.awt.BorderLayout.CENTER);
    this.pack();
}

