/**
 * Renders the board on the given graphics context to the given dimensions.
 *
 * @param board
 * 		The board to render.
 * @param graphics
 * 		The graphics context to draw on.
 * @param window
 * 		The dimensions to scale the rendered board to.
 */
private void render(nl.tudelft.jpacman.board.Board board, java.awt.Graphics graphics, java.awt.Dimension window) {
    int cellW = window.width / board.getWidth();
    int cellH = window.height / board.getHeight();
    graphics.setColor(nl.tudelft.jpacman.ui.BoardPanel.BACKGROUND_COLOR);
    graphics.fillRect(0, 0, window.width, window.height);
    for (int y = 0; y < board.getHeight(); y++) {
        for (int x = 0; x < board.getWidth(); x++) {
            int cellX = x * cellW;
            int cellY = y * cellH;
            nl.tudelft.jpacman.board.Square square = board.squareAt(x, y);
            this.render(square, graphics, cellX, cellY, cellW, cellH);
        }
    }
}

